import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import "./App.css";

import { Tabs, Tab } from 'react-bootstrap'; 


//Import Components
import AddCompany from './components/AddCompany';
import CompanyProfile from './components/CompanyProfile';


class App extends Component {

  state = {
    companies: []
  }

  addCompany = async ticker => {
    const request = await fetch(`http://localhost:8080/company`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },

      body: JSON.stringify({
        ticker: ticker
      })
    });

    const data = await request.json();
    this.setState({ companies: [...this.state.companies, data] })
  }


  render() {   

    var companyProfiles = this.state.companies.map(company =>
      <Tab eventkey={company.company_overview.Name}>
        <CompanyProfile company={company} />
      </Tab>
    );

    return (
      <div>
        <h1>Dashboard</h1>
        <AddCompany addCompany={this.addCompany}/>
        <Tabs>
          {companyProfiles}
        </Tabs>
      </div>
    );
  }
}
export default App;
