import React, { Component } from 'react'
import { Table } from 'react-bootstrap';

export default class BalanceSheet extends Component {
    renderAllValues = (keys, annualReport) => {
        const data =  keys.map(key => {
            return <td key={key}>{annualReport[key]}</td>
        });
        
        return data;
    }
    
    renderTable = (keys, annualReports) => {
        const display = keys.map(key => 
        
            <tr>
                <td key={key}>{key}</td>
                {this.renderAllValues(keys, annualReports[0])}
            </tr>
            
              
        );

        return display; 
    }   


    
    
    
    render() {



        const annualReports = this.props.balance_sheet.annualReports; 
        
        console.log(annualReports);
        //Push all keys onto an array
        let keys = [];
    
        Object.keys(annualReports[0]).forEach(key => {
            if(annualReports[0][key] !== 'None') {
                keys.push(key)     
            }
        }); 
        
        console.log(keys);
        console.log(annualReports);
    
        return (
            <div>
                <h1>Balance Sheet</h1>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Value</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderTable(keys, annualReports)}
                    </tbody> 
                </Table>
            </div>
           
        )
        
    }
}
