import React, { Component } from 'react';
import Tabs from "./components/Tabs";
import "./App.css";

//Import Components
import AddCompany from './components/AddCompany';


//Import Financial Statement Components
import  BalanceSheet from './components/FinancialStatements/BalanceSheet'; 


const getStock = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/stock`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker,
      type: "daily"
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};

const getCompanyOverview = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/company-overview`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};


const getIncomeStatement = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/income-statement`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};

const getBalanceSheet = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/balance-sheet`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  return data.data;
};


const getCashFlow = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/cash-flow`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};


class App extends Component {

  state = {
    companies: []
  }

  addCompany = (ticker) => {
    getBalanceSheet(ticker).then(res => {
      const company = {
        ticker, 
        data: res
      }

      this.setState({companies: [...this.state.companies, company]});
      console.log(this.state);

    });
  }


  render() {
    return (
      <div>
        <h1>Dashboard</h1>
        <Tabs> 
          <div label="Home"> 
            <h2>Hey pick two companies!</h2>
            <AddCompany addCompany={this.addCompany}/>

          </div> 
          <div label="Company Name - Financial Statements"> 
            <h4>Comp Name Balance Sheet</h4> 
            <BalanceSheet />
            <h4>Comp Name Income Statment</h4> 
            <div id="income_statement"></div>
            <h4>Comp Name Cash Flow</h4>
            <div id="cash_flow"></div>

          </div> 
          <div label="Financial Statement Analysis">
            <h1>Comp Name 1 Analysis</h1>  
            <h1>Comp Name 2 Analysis</h1> 
          </div> 
          <div label="Stock Price Analysis">
            <h1>Comp Name 1 Analysis</h1>  
            <h1>Comp Name 2 Analysis</h1> 
          </div> 
        </Tabs> 


      </div>
    );
  }
}
export default App;
