import React, { Component } from 'react'
import { Table } from 'react-bootstrap';

export default class BalanceSheet extends Component {
    render() {
        const annualReports = this.props.balance_sheet.annualReports; 
        
        
        //Push all keys onto an array
        let keys = [];
        
        Object.keys(annualReports).forEach(key => {
            if(annualReports[key] !== 'None') {
                keys.push(key)     
            }
        }); 

        var renderAllValues = (keys, annualReports) => {
            annualReports.map( annualReport => {    
               keys.map(key => {
                    return <td key={key}>{annualReport[key]}</td>
                });
            });
        }
        
        var renderTable = (keys, annualReports) => {
            keys.map(key => {
                return (
                    <tr>
                        <td key={key}>{key}</td>
                        {renderAllValues(keys, annualReports)}
                    </tr>
                )
                  
            });
        }



        return (
            <div>
                <h1>Balance Sheet</h1>
                <Table striped bordered hover>
                    <thread>
                        <tr>
                            <th>Title</th>
                            <th>Value</th>
                        </tr>
                    </thread>
                    <tbody>
                        {renderTable(keys, annualReports)}
                    </tbody> 
                </Table>
            </div>
           
        )
        
    }
}
