import React, { Component } from 'react'

export default class BalanceSheet extends Component {
    render() {
        
        //Get the passed in prop
        const balance_sheet = this.props.balance_sheet; 
        let keys = [];
        
        //Push all keys onto an array
        Object.keys(balance_sheet).forEach(key =>
            keys.push(key)     
        ); 
        
        //For each key create a display
        const display = keys.map(key => 
            <p key={key}>{key}: {balance_sheet[key]}</p>
        );

        return (
            <div>
                <h1>Company balance_sheet</h1>
                {display}
            </div>
        )
        
    }
}
