import React, { Component } from 'react';
import "./App.css";

import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

//Import Components
import AddCompany from './components/AddCompany';
import CompanyProfile from './components/CompanyProfile';


class App extends Component {

  state = {
    companies: []
  }

  addCompany = async ticker => {
    const request = await fetch(`http://localhost:8080/company`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },

      body: JSON.stringify({
        ticker: ticker
      })
    });

    const data = await request.json();
    this.setState({ companies: [...this.state.companies, data] })
  }


  render() {
    if(!Array.isArray(this.state.companies)) {
      return (
        <div>
          <h1>Dashboard</h1>
          <Tabs>
            <TabList>
              <Tab>Home</Tab>
            </TabList>

            <TabPanel>
              <AddCompany addCompany={this.addCompany}/>
            </TabPanel>
          </Tabs>
        </div>
      );
    } else {


      var companyNames = this.state.companies.map(company =>
        <Tab key={company.company_overview.Symbol}>{company.company_overview.Name} - Profile</Tab>
      );

      var companyProfiles = this.state.companies.map(company => 
        <TabPanel key={company.company_overview.Symbol}>  
          <CompanyProfile company={company} />  
        </TabPanel>
      ) 

      return (
        <div>
          <h1>Dashboard</h1>
          <Tabs>
            <TabList>
              <Tab>Home</Tab>
              {companyNames}
            </TabList>

            <TabPanel>
              <AddCompany addCompany={this.addCompany}/>
            </TabPanel>
            {companyProfiles}
          </Tabs>
        </div>
      );
    }
  }
}
export default App;
