import React from "react";

const getStock = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/stock`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker,
      type: "daily"
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};

const getCompanyOverview = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/company-overview`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};


const getIncomeStatement = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/income-statement`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};

const getBalanceSheet = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/balance-sheet`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};


const getCashFlow = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/cash-flow`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};






class App extends React.Component {
  render() {
    return (
      <div>
        <h2>Hey!</h2>
        <button onClick={() => getStock("PFSI", 'daily')}>Get stock</button>
        <button onClick={() => getBalanceSheet("PFSI")}>Get Balance Sheet</button>
        <button onClick={() => getIncomeStatement("PFSI")}>Get Income Statement</button>
        <button onClick={() => getCashFlow("PFSI")}>Get Cash Flow</button>
        <button onClick={() => getCompanyOverview("PFSI")}>Get Company Overview</button>        
      </div>
    );
  }
}
export default App;
