import React, { Component } from 'react'
import { Table } from 'react-bootstrap';

export default class BalanceSheet extends Component {
    render() {
        
        //Get the passed in prop
        const balance_sheet = this.props.balance_sheet.annualReports[0]; 
        let keys = [];
        
        //Push all keys onto an array
        Object.keys(balance_sheet).forEach(key => {
            if(balance_sheet[key] !== 'None') {
                keys.push(key)     
            }
        }); 
        
        //For each key create a display
        const display = keys.map(key =>
            <tr>  
                <td key={key}>{key}</td>
                <td key={key}>{balance_sheet[key]}</td>
            </tr>
        );

        return (
            <div>
                <h1>Balance Sheet</h1>
                <Table striped bordered hover>
                    <thread>
                        <th>Title</th>
                        <th>Value</th>
                    </thread>
                    <tbody>
                        {display}
                    </tbody> 
                </Table>
            </div>
           
        )
        
    }
}
