import React from "react";
import Tabs from "./components/Tabs";
import "./App.css";
import ReactDOM from 'react-dom'

//Import Financial Statement Components
import  BalanceSheet from './components/FinancialStatements/BalanceSheet'; 


const getStock = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/stock`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker,
      type: "daily"
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};

const getCompanyOverview = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/company-overview`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};


const getIncomeStatement = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/income-statement`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};

const getBalanceSheet = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/balance-sheet`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data)

  const annualReports = data.data.annualReports;
  const balanceSheet = annualReports[0];

  let div = document.getElementById("balance_sheet");

  for (let lineItem in balanceSheet) {
    var value = balanceSheet[lineItem];

    let element = document.createElement('p');
    element.innerHTML = lineItem + ' : ' + value;
    console.log(element)
    if(element != null) {
      div.appendChild(element);
    }
  }


};


const getCashFlow = async ticker => {
  console.log("Getting data");
  const request = await fetch(`http://localhost:8080/cash-flow`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json"
    },

    body: JSON.stringify({
      ticker: ticker
    })
  });

  const data = await request.json();
  console.log(data);
  return data;
};




class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    getIncomeStatement(this.state.value)
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <h1>Dashboard</h1>
        <Tabs> 
          <div label="Home"> 
            <h2>Hey pick two companies!</h2>
            <form onSubmit={this.handleSubmit}>
              <label>
                Input Ticker:
                <input type="text" value={this.state.value} onChange={this.handleChange} />
              </label>
              <input type="submit" value="Submit" />
            </form>
            <button onClick={() => getStock("PFSI", 'daily')}>Get stock</button>
            <button onClick={() => getBalanceSheet("PFSI")}>Get Balance Sheet</button>
            <button onClick={() => getIncomeStatement("PFSI")}>Get Income Statement</button>
            <button onClick={() => getCashFlow("PFSI")}>Get Cash Flow</button>
            <button onClick={() => getCompanyOverview("PFSI")}>Get Company Overview</button>
          </div> 
          <div label="Company Name - Financial Statements"> 
            <h4>Comp Name Balance Sheet</h4> 
            <BalanceSheet />
            <h4>Comp Name Income Statment</h4> 
            <div id="income_statement"></div>
            <h4>Comp Name Cash Flow</h4>
            <div id="cash_flow"></div>

          </div> 
          <div label="Financial Statement Analysis">
            <h1>Comp Name 1 Analysis</h1>  
            <h1>Comp Name 2 Analysis</h1> 
          </div> 
          <div label="Stock Price Analysis">
            <h1>Comp Name 1 Analysis</h1>  
            <h1>Comp Name 2 Analysis</h1> 
          </div> 
        </Tabs> 


      </div>
    );
  }
}
export default App;
