import React, { Component } from 'react';
import "./App.css";

import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

//Import Components
import AddCompany from './components/AddCompany';
import CompanyProfile from './components/CompanyProfile';


class App extends Component {

  state = {
    companies: []
  }

  addCompany = async ticker => {
    const request = await fetch(`http://localhost:8080/company`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },

      body: JSON.stringify({
        ticker: ticker
      })
    });

    const data = await request.json();
    console.log(data.company_overview.Symbol)

  }


  render() {

    var companyNames = this.state.companies.map(company =>
      <Tab>{company.company_overview.Symbol}</Tab>
    );

    var companyProfiles = this.state.companies.map(company => 
      <div label="CompanyName">  
        <CompanyProfile company={company} />  
      </div>
    ) 

    return (
      <div>
        <h1>Dashboard</h1>
        <Tabs>
          <TabList>
            <Tab>Home</Tab>
            <Tab>Title 2</Tab>
          </TabList>

          <TabPanel>
            <AddCompany addCompany={this.addCompany}/>
          </TabPanel>
          <TabPanel>
            <h2>Any content 2</h2>
          </TabPanel>
        </Tabs>
      </div>
    );
  }
}
export default App;
