import React, { Component } from 'react'
import { Table } from 'react-bootstrap';

export default class BalanceSheet extends Component {
    render() {
        var display  = '';
        //Get the passed in prop
        this.props.balance_sheet.annualReports.map(annualReports =>  {
            console.log(annualReports);
            let keys = [];
            
            //Push all keys onto an array
            Object.keys(annualReports).forEach(key => {
                if(annualReports[key] !== 'None') {
                    keys.push(key)     
                }
            }); 
            


            //For each key create a display
            var new_display = keys.map(key =>
                <tr>  
                    <td key={key}>{key}</td>
                    <td key={key}>{annualReports[key]}</td>
                </tr>
            );

            display = display + new_display;
        });

        return (
            <div>
                <h1>Balance Sheet</h1>
                <Table striped bordered hover>
                    <thread>
                        <th>Title</th>
                        <th>Value</th>
                    </thread>
                    <tbody>
                        {display}
                    </tbody> 
                </Table>
            </div>
           
        )
        
    }
}
